mapper.select:
SELECT screen_id, COUNT(*) AS count
FROM seat
WHERE screen_id = #{screenId}
GROUP BY screen_id;

SELECT schedule_id
FROM screen_schedule
WHERE running_date BETWEEN #{start} AND #{end}

SELECT created_at
FROM reservation
WHERE reservation_id = #{reservationId}

SELECT point
FROM user
WHERE user_id = #{userId}

mapper.update:
UPDATE user
SET point = #{point}
WHERE user_id = #{userId}

mapper.insert:
INSERT INTO payment_mobile
SET payment_id = #{paymentId},
    carrier_code = #{carrierCode},
    phone_number = #{phone}
    approval_code = #{approvalCode}
